import React, { useEffect, useState } from 'react';
import { io, Socket } from 'socket.io-client';
import axios from 'axios';
import { Message } from './types';

const socket: Socket = io('http://localhost:3001'); // Porta NestJS WebSocket

const ChatBox: React.FC = () => {
  const [sender, setSender] = useState('');
  const [text, setText] = useState('');
  const [messages, setMessages] = useState<Message[]>([]);

  useEffect(() => {
    console.log('ðŸ“¡ Connessione WebSocket avviata');

    socket.on('message', (message: Message) => {
      console.log('ðŸ“¥ Messaggio ricevuto via WebSocket:', message);

      // Mostra subito il messaggio dellâ€™utente
      if (!message.isGenerated) {
        setMessages((prev) => [...prev, message]);
      }
    });

    return () => {
      socket.off('message');
      console.log('ðŸ”Œ Connessione WebSocket chiusa');
    };
  }, []);

  const sendMessage = async () => {
    if (!sender.trim() || !text.trim()) {
      console.warn('âš ï¸ Nome utente o messaggio vuoto, annullato.');
      return;
    }

    const userMsg: Message = {
      sender,
      text,
      isGenerated: false,
    };

    console.log('âœ‰ï¸ Invio messaggio utente:', userMsg);
    setMessages((prev) => [...prev, userMsg]);

    socket.emit('message', userMsg);
    console.log('ðŸ“¡ Messaggio inviato via WebSocket');

    const loadingMsg: Message = {
      sender: 'AI',
      text: 'Sta scrivendo...',
      isGenerated: true,
    };

    setMessages((prev) => [...prev, loadingMsg]);
    console.log('â³ Messaggio "Sta scrivendo..." aggiunto');

    try {
      console.log('ðŸ”— Chiamata a FastAPI...');
      const response = await axios.post('http://localhost:8000/ai', {
        message: text,
      });
      console.log('âœ… Risposta FastAPI:', response.data);

      const aiMsg: Message = {
        sender: 'AI',
        text: response.data.response,
        isGenerated: true,
      };

      setMessages((prev) => {
        const updated = [...prev];
        const index = updated.findIndex(
          (m) => m.sender === 'AI' && m.text === 'Sta scrivendo...'
        );
        if (index !== -1) updated.splice(index, 1);
        return [...updated, aiMsg];
      });

      console.log('ðŸ¤– Risposta AI aggiunta alla chat:', aiMsg);
    } catch (err: any) {
      console.error('âŒ Errore FastAPI:', err.response?.data || err.message || err);

      setMessages((prev) => [
        ...prev.filter((m) => m.text !== 'Sta scrivendo...'),
        {
          sender: 'AI',
          text: '[Errore nella risposta AI]',
          isGenerated: true,
        },
      ]);
    }

    setText('');
  };

  return (
    <div className="chat-container">
      <h1>Chat Realtime + AI</h1>
      <div className="input-group">
        <input
          value={sender}
          onChange={(e) => setSender(e.target.value)}
          placeholder="Your name"
        />
        <input
          value={text}
          onChange={(e) => setText(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
          placeholder="Message"
        />
        <button onClick={sendMessage}>Send</button>
      </div>

      <ul>
        {messages.map((msg, i) => (
          <li key={i} className={msg.isGenerated ? 'ai' : 'user'}>
            <div className="message-row">
              <span className="avatar">{msg.isGenerated ? 'ðŸ¤–' : 'ðŸ‘¤'}</span>
              <div className="bubble">
                <strong>{msg.sender}:</strong> {msg.text}
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ChatBox;
