import React, { useState } from 'react';
import axios from 'axios';
import { Message } from './types';

const ChatBox: React.FC = () => {
  const [sender, setSender] = useState<string>('');
  const [text, setText] = useState<string>('');
  const [messages, setMessages] = useState<Message[]>([]);

  const sendMessage = async () => {
    if (!sender.trim() || !text.trim()) return;

    const userMsg: Message = {
      sender,
      text,
      isGenerated: false,
    };

    setMessages((prev) => [...prev, userMsg]);

    const loadingId = `loading-${Date.now()}`; // ID unico
    const loadingMsg: Message & { id: string } = {
      id: loadingId,
      sender: 'AI',
      text: 'Sta scrivendo...',
      isGenerated: true,
    };

    setMessages((prev) => [...prev, loadingMsg]);

    try {
      const response = await axios.post('http://localhost:8000/ai', {
        message: text,
      });

      setTimeout(() => {
        setMessages((prev) =>
          prev
            .filter((m) => !(m as any).id || (m as any).id !== loadingId)
            .concat({
              sender: 'AI',
              text: response.data.response,
              isGenerated: true,
            })
        );
      }, 500);
    } catch (err) {
      console.error('Errore FastAPI:', err);
    }

    setText('');
  };

  return (
    <div className="chat-container">
      <h1>Chat Box</h1>
      <div className="input-group">
        <input
          value={sender}
          onChange={(e) => setSender(e.target.value)}
          placeholder="Your name"
        />
        <input
          value={text}
          onChange={(e) => setText(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
          placeholder="Message"
        />
        <button onClick={sendMessage}>Send</button>
      </div>

      <ul>
        {messages.map((msg, i) => (
          <li key={i} className={msg.isGenerated ? 'ai' : 'user'}>
            <div className="message-row">
              <span className="avatar">{msg.isGenerated ? 'ðŸ¤–' : 'ðŸ‘¤'}</span>
              <div className="bubble">
                <strong>{msg.sender}:</strong> {msg.text}
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ChatBox;
