import React, { useEffect, useState } from 'react';
import { io, Socket } from 'socket.io-client';
import axios from 'axios';
import { Message } from './types';

const socket: Socket = io('http://localhost:3001'); // Porta NestJS WebSocket

const ChatBox: React.FC = () => {
  const [sender, setSender] = useState('');
  const [text, setText] = useState('');
  const [messages, setMessages] = useState<(Message & { id?: string })[]>([]);

  useEffect(() => {
    socket.on('message', (message: Message) => {
      // Evita duplicati o loop infiniti
      if (!message.isGenerated) {
        setMessages((prev) => [...prev, message]);
        requestAIResponse(message.text);
      }
    });

    return () => {
      socket.off('message');
    };
  }, []);

  const requestAIResponse = async (msgText: string) => {
    const loadingId = `loading-${Date.now()}`;
    const loadingMsg: Message & { id: string } = {
      id: loadingId,
      sender: 'AI',
      text: 'Sta scrivendo...',
      isGenerated: true,
    };

    setMessages((prev) => [...prev, loadingMsg]);

    try {
      const response = await axios.post('http://localhost:8000/ai', {
        message: msgText,
      });

      const aiMsg: Message = {
        sender: 'AI',
        text: response.data.response,
        isGenerated: true,
      };

      // Rimuove il messaggio "Sta scrivendo..." e aggiunge la risposta
      setMessages((prev) =>
        prev.filter((m) => m.id !== loadingId).concat(aiMsg)
      );
    } catch (err) {
      console.error('âŒ Errore FastAPI:', err);
      setMessages((prev) =>
        prev.filter((m) => m.id !== loadingId).concat({
          sender: 'AI',
          text: '[Errore durante la risposta AI]',
          isGenerated: true,
        })
      );
    }
  };

  const sendMessage = () => {
    if (!sender.trim() || !text.trim()) return;

    const userMsg: Message = {
      sender,
      text,
      isGenerated: false,
    };

    // Invia il messaggio agli altri tramite WebSocket
    socket.emit('message', userMsg);
    setText('');
  };

  return (
    <div className="chat-container">
      <h1>Chat Realtime + AI</h1>
      <div className="input-group">
        <input
          value={sender}
          onChange={(e) => setSender(e.target.value)}
          placeholder="Your name"
        />
        <input
          value={text}
          onChange={(e) => setText(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
          placeholder="Message"
        />
        <button onClick={sendMessage}>Send</button>
      </div>

      <ul>
        {messages.map((msg, i) => (
          <li key={i} className={msg.isGenerated ? 'ai' : 'user'}>
            <div className="message-row">
              <span className="avatar">{msg.isGenerated ? 'ðŸ¤–' : 'ðŸ‘¤'}</span>
              <div className="bubble">
                <strong>{msg.sender}:</strong> {msg.text}
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ChatBox;
